import React, { useState, useEffect } from 'react';
import './App.css';
const App = () => {
  const [quote, setquote] = useState([])

  const Modify = () => {
    const key = { author }
    fetch("https://api.quotable.io/random")
      .then((response) => response.json())
      .then((data) => setquote(data));
  };

  useEffect(() => {
    Modify();
  }, []);

  const getnewQuote = () => {
    Modify();
  }

  const { content, author } = quote;
  return (
    <div className='mainbox'>
      <div className="text">
        <p>{content}</p>
      </div>
      <div className="author" />
      <h5>{author}</h5>
      <button onClick={getnewQuote} className='newquote'>ROLL ON</button>
      {quote}
    </div>
  );
}


export default App;
