import React,{useState} from 'react';
import './App.css';
const App=()=>{
const [quote, setquote] = useState('')
const modify =()=>{
fetch("https://type.fit/api/quotes")
  .then(function(response) {
    return response.json();
  })
  .then(function(data) {
    console.log(data);
    setquote(data);
  });
}
return(
<div className='mainbox'>
<button onClick={()=>setquote(modify)} className='newquote'>ROLL ON</button>
{quote}
</div>
);
}


export default App;
